SUBROUTINE Adist(xx, yy, dist)
IMPLICIT NONE

REAL_T, INTENT (IN) :: xx
REAL_T, INTENT (IN) :: yy
REAL_T, INTENT (INOUT) :: dist

REAL_T, DIMENSION(4) :: xvec
REAL_T, DIMENSION(4) :: yvec
REAL_T, DIMENSION(4) :: nx
REAL_T, DIMENSION(4) :: ny
REAL_T, DIMENSION(4) :: m

REAL_T :: px, py
REAL_T :: vx, vy
REAL_T :: phi_i, maxval
REAL_T :: mhat
REAL_T :: dist1, dist2, dist3
REAL_T :: eps
INTEGER_T :: i

eps = 0.01
dist = -999.9e9
dist1 = -999.9e9
dist2 = -999.9e9
dist3 = -999.9e9

! Big Triangle !
xvec(1) = 0.0
xvec(2) = 1.0
xvec(3) = -1.0

yvec(1) = 3.0
yvec(2) = 0.0
yvec(3) = 0.0

nx(1) = 3.0/sqrt(10.0)
nx(2) = 0.0
nx(3) = -3.0/sqrt(10.0)

ny(1) = 1.0/sqrt(10.0)
ny(2) = -1.0
ny(3) = 1.0/sqrt(10.0)

m(1) = -3.0
m(2) = 0.0
m(3) = 3.0

maxval = -999.9
do i = 1, 3
	if (i /= 2) then
		!write(*,*) 'i = ', i
		mhat = -1.0/m(i)
		px = (m(i)*xvec(i) - mhat*xx + yy - yvec(i))/(m(i) - mhat)
		py = m(i)*(px - xvec(i)) + yvec(i)
	else	! i == 2, horizontal edge
		!write(*,*) 'i = ', i
		px = xx
		py = yvec(i)
	endif
	
	vx = xx - px
	vy = yy - py
	phi_i = vx*nx(i) + vy*ny(i)
	
	!perr = abs(m(i) - (py - yvec(i))/(px - xvec(i)))
	!write(*,*) 'Projection error = ', perr
	
	if(phi_i > maxval) then
		maxval = phi_i
		!write(*,*) 'i = ', i, 'new phi_i = ', phi_i
	endif
	
	!write(*,*) 'i = ', i, 'nx = ', nx(i), 'ny = ', ny(i), 'vx = ', vx, 'vy = ', vy
enddo

dist1 = maxval
! End Big Triangle !

! Trapezoid !
xvec(1) = 4.0/9.0
xvec(2) = (2.0 + eps)/3.0
xvec(3) = -(2.0 + eps)/3.0
xvec(4) = -4.0/9.0

yvec(1) = 2.0/3.0
yvec(2) = -eps
yvec(3) = -eps
yvec(4) = 2.0/3.0

nx(1) = -3.0/sqrt(10.0)
nx(2) = 0.0
nx(3) = 3.0/sqrt(10.0)
nx(4) = 0.0

ny(1) = -1.0/sqrt(10.0)
ny(2) = 1.0
ny(3) = -1.0/sqrt(10.0)
ny(4) = -1.0

m(1) = -3.0
m(2) = 0.0
m(3) = 3.0
m(4) = 0.0

maxval = 999.9
do i = 1, 4
	if ((i == 1) .OR. (i == 3)) then
		!write(*,*) 'i = ', i
		mhat = -1.0/m(i)
		px = (m(i)*xvec(i) - mhat*xx + yy - yvec(i))/(m(i) - mhat)
		py = m(i)*(px - xvec(i)) + yvec(i)
	else	! i == 2 || 4, horizontal edge
		!write(*,*) 'i = ', i
		px = xx
		py = yvec(i)
	endif
	
	vx = xx - px
	vy = yy - py
	phi_i = vx*nx(i) + vy*ny(i)
	
	!perr = abs(m(i) - (py - yvec(i))/(px - xvec(i)))
	!write(*,*) 'Projection error = ', perr
	
	if(phi_i < maxval) then
		maxval = phi_i
		!write(*,*) 'i = ', i, 'new phi_i = ', phi_i
	endif
	
	!write(*,*) 'i = ', i, 'nx = ', nx(i), 'ny = ', ny(i), 'vx = ', vx, 'vy = ', vy
enddo

dist2 = maxval
! End Trapezoid !

! Little Triangle !
xvec(1) = 0.0
xvec(2) = 1.0/3.0
xvec(3) = -1.0/3.0

yvec(1) = 2.0
yvec(2) = 1.0
yvec(3) = 1.0

nx(1) = -3.0/sqrt(10.0)
nx(2) = 0.0
nx(3) = 3.0/sqrt(10.0)

ny(1) = -1.0/sqrt(10.0)
ny(2) = 1.0
ny(3) = -1.0/sqrt(10.0)

m(1) = -3.0
m(2) = 0.0
m(3) = 3.0

maxval = 999.9
do i = 1, 3
	if ((i == 1) .OR. (i == 3)) then
		!write(*,*) 'i = ', i
		mhat = -1.0/m(i)
		px = (m(i)*xvec(i) - mhat*xx + yy - yvec(i))/(m(i) - mhat)
		py = m(i)*(px - xvec(i)) + yvec(i)
	else	! i == 2, horizontal edge
		!write(*,*) 'i = ', i
		px = xx
		py = yvec(i)
	endif
	
	vx = xx - px
	vy = yy - py
	phi_i = vx*nx(i) + vy*ny(i)
	
	!perr = abs(m(i) - (py - yvec(i))/(px - xvec(i)))
	!write(*,*) 'Projection error = ', perr
	
	if(phi_i < maxval) then
		maxval = phi_i
		!write(*,*) 'i = ', i, 'new phi_i = ', phi_i
	endif
	
	!write(*,*) 'i = ', i, 'nx = ', nx(i), 'ny = ', ny(i), 'vx = ', vx, 'vy = ', vy
enddo

dist3 = maxval
! End Little Triangle !

dist = max(dist1, dist2, dist3)

END SUBROUTINE Adist



      function CLS(phi,eps)
      IMPLICIT NONE
      REAL_T CLS,phi,eps,temp

      temp=phi/(two*eps)
      CLS=half*(sinh(temp)/cosh(temp)+one)

      return
      end function CLS

      function myfact(n)
      INTEGER_T myfact,i,n

      if (n.eq.0) then
       myfact=1
      else
       myfact=1
       do i=2,n
        myfact=myfact*i 
       enddo
      endif

      return
      end function myfact


