        dist=distbatch(1)
       else if(probtype.eq.72) then
        xmin=xblob-radblob
        xmax=xblob+radblob
        ymin=zblob-1.0e+10
        ymax=zblob+radblob
        call squaredist(x,y,xmin,xmax,ymin,ymax,dist)
