       k1lo=-1
       k1hi=1
      else
       print *,"dimension bust"
       stop
      endif

      do i1=-1,1
      do j1=-1,1
      do k1=k1lo,k1hi
       do isten=-1,1
        dir2=1
        xsten2(isten,dir2)=xsten(isten+2*i1,dir2)
        dir2=2
        xsten2(isten,dir2)=xsten(isten+2*j1,dir2)
        if (SDIM.eq.3) then
         dir2=SDIM
         xsten2(isten,dir2)=xsten(isten+2*k1,dir2)
