        lsgrid,vfrac, &
        facearea,centroid,areacentroid,nmat,EBVOFTOL,SDIM)

      do im=1,nmat
       do dir2=1,SDIM
        cenbc(im,dir2)=centroid(im,dir2)-xsten(0,dir2)
       enddo
      enddo

      return
